import React, { useState, useEffect } from 'react';
import { Star, X, AlertCircle, Facebook } from 'lucide-react';

const VideoCard = ({ video, onPlay }) => (
  <div className="flex flex-col w-64 m-2 cursor-pointer" onClick={() => onPlay(video)}>
    <div className="relative w-full h-36">
      {video.thumbnail.startsWith('<iframe') ? (
        <div dangerouslySetInnerHTML={{ __html: video.thumbnail }} />
      ) : (
        <img src={video.thumbnail} alt={video.title} className="w-full h-full object-cover rounded-md" />
      )}
    </div>
    <h3 className="mt-2 text-sm font-semibold">{video.title}</h3>
    <div className="flex mt-1">
      {[...Array(5)].map((_, i) => (
        <Star
          key={i}
          size={16}
          fill={i < video.rating ? 'currentColor' : 'none'}
          className={i < video.rating ? 'text-yellow-400' : 'text-gray-300'}
        />
      ))}
    </div>
  </div>
);

const VideoPlayer = ({ video, onClose }) => {
  const getEmbedUrl = (url) => {
    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const videoId = url.split('v=')[1] || url.split('/').pop();
      return `https://www.youtube.com/embed/${videoId}`;
    }
    if (url.includes('vimeo.com')) {
      const videoId = url.split('/').pop();
      return `https://player.vimeo.com/video/${videoId}`;
    }
    if (url.includes('facebook.com')) {
      return `https://www.facebook.com/plugins/video.php?href=${encodeURIComponent(url)}&show_text=false&t=0`;
    }
    return url;
  };

  const embedUrl = getEmbedUrl(video.link);

  return (
    <div className="fixed inset-0 bg-black flex items-center justify-center z-50">
      <button onClick={onClose} className="absolute top-4 right-4 text-white hover:text-gray-300">
        <X size={24} />
      </button>
      <div className="w-full h-full max-w-6xl max-h-[90vh]">
        {embedUrl ? (
          <iframe 
            src={embedUrl} 
            title={video.title} 
            className="w-full h-full"
            allowFullScreen
          ></iframe>
        ) : (
          <div className="w-full h-full flex items-center justify-center bg-gray-100">
            <div className="text-center">
              <AlertCircle size={48} className="mx-auto mb-4 text-yellow-500" />
              <p>This video cannot be embedded.</p>
              <a href={video.link} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline mt-2 inline-block">
                Open video in new tab
              </a>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const VideoLibrary = () => {
  const [videosByCategory, setVideosByCategory] = useState({});
  const [playingVideo, setPlayingVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      setLoading(true);
      const response = await fetch('/.netlify/functions/getVideos');
      if (!response.ok) throw new Error('Failed to fetch videos');
      const data = await response.json();

      const groupedVideos = data.reduce((acc, video) => {
        if (!acc[video.category]) {
          acc[video.category] = [];
        }
        acc[video.category].push({
          ...video,
          thumbnail: getVideoThumbnail(video.link)
        });
        return acc;
      }, {});

      setVideosByCategory(groupedVideos);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const getVideoThumbnail = (url) => {
    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const videoId = url.split('v=')[1] || url.split('/').pop();
      return `https://img.youtube.com/vi/${videoId}/0.jpg`;
    }
    if (url.includes('vimeo.com')) {
      const videoId = url.split('/').pop();
      return `https://vumbnail.com/${videoId}.jpg`;
    }
    if (url.includes('facebook.com')) {
        return <Facebook size={48} color="red" />
    //   return `<iframe src="https://www.facebook.com/plugins/video.php?href=${url}&width=450&show_text=false&height=300&appId" width="450" height="300" style="border:none;overflow:hidden" scrolling="no" frameborder="0" allowfullscreen="true" allow="autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share"></iframe>`;
    }
    return '/api/placeholder/300/200';
  };

  if (loading) return <div className="flex justify-center items-center h-screen">Loading...</div>;
  if (error) return <div className="flex justify-center items-center h-screen text-red-500">Error: {error}</div>;

  return (
    <div className="p-6">
      {Object.entries(videosByCategory).map(([category, videos]) => (
        <div key={category} className="mb-8">
          <h2 className="text-2xl font-bold mb-4">{category}</h2>
          <div className="flex flex-wrap -mx-2">
            {videos.map(video => (
              <VideoCard key={video.id} video={video} onPlay={setPlayingVideo} />
            ))}
          </div>
        </div>
      ))}
      {playingVideo && <VideoPlayer video={playingVideo} onClose={() => setPlayingVideo(null)} />}
    </div>
  );
};

export default VideoLibrary;
