import React from 'react';
import { Star, Facebook } from 'lucide-react';
import '../styles/VideoCard.css';

const VideoCard = ({ video, onClick }) => {

    const getVideoThumbnail = (url) => {
        if (url.includes('youtube.com') || url.includes('youtu.be')) {
          const videoId = url.split('v=')[1] || url.split('/').pop();
          return `https://img.youtube.com/vi/${videoId}/0.jpg`;
        }
        if (url.includes('vimeo.com')) {
          const videoId = url.split('/').pop();
          return `https://vumbnail.com/${videoId}.jpg`;
        }
        if (url.includes('facebook.com')) {
          return (
            `<iframe src="https://www.facebook.com/plugins/video.php?href=${url}&width=300&show_text=false&appId" width="300" height="889" style="border:none;overflow:hidden" scrolling="no" frameborder="0" allowfullscreen="true" allow="autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share" allowFullScreen="true"></iframe>`
          );
        }
        return '/api/placeholder/300/200';
      };

    return (
        <div className="video-card" onClick={onClick}>
            <div>
            {React.isValidElement(getVideoThumbnail(video.link)) ? (
                getVideoThumbnail(video.link)
            ) : (
            <img src={getVideoThumbnail(video.link)} alt={video.title} className="w-full h-full object-cover" />
            )}    
            </div>  
            <h3>{video.title}</h3>
            <div className="flex mt-1">
                {[...Array(5)].map((_, i) => (
                <Star
                    key={i}
                    size={16}
                    fill={i < video.rating ? 'currentColor' : 'none'}
                    className={i < video.rating ? 'text-yellow-400' : 'text-gray-300'}
                />
                ))}
            </div> 
        </div>
    );
  };

  export default VideoCard;
