import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import VideoLibrary from './components/VideoLibrary';
import AddEditVideo from './components/AddEditVideo';
import './App.css';

const App = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('/.netlify/functions/getVideos');
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  return (
    <Router>
      <div className="app">
        <nav>
          <Link to="/">Video Library</Link>
          <Link to="/add">Add New Video</Link>
        </nav>

        <Routes>
          <Route path="/" element={<VideoLibrary videos={videos} onUpdate={fetchVideos} />} />
          <Route path="/add" element={<AddEditVideo onSave={fetchVideos} />} />
          <Route path="/edit/:id" element={<AddEditVideo videos={videos} onSave={fetchVideos} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
