import React, { useState } from 'react';
import { Star, X } from 'lucide-react';

// Assuming you have a list of videos grouped by category
const videosByCategory = {
  'Action': [
    { id: 1, title: 'Die Hard', link: 'https://example.com/die-hard', rating: 4, thumbnail: '/api/placeholder/300/200' },
    { id: 2, title: 'Mad Max', link: 'https://example.com/mad-max', rating: 5, thumbnail: '/api/placeholder/300/200' },
  ],
  'Comedy': [
    { id: 3, title: 'Bridesmaids', link: 'https://example.com/bridesmaids', rating: 3, thumbnail: '/api/placeholder/300/200' },
    { id: 4, title: 'The Hangover', link: 'https://example.com/the-hangover', rating: 4, thumbnail: '/api/placeholder/300/200' },
  ],
  // Add more categories and videos as needed
};

const VideoCard = ({ video, onPlay }) => (
  <div className="flex flex-col w-48 mr-4 cursor-pointer" onClick={() => onPlay(video)}>
    <img src={video.thumbnail} alt={video.title} className="w-full h-32 object-cover rounded-md" />
    <h3 className="mt-2 text-sm font-semibold">{video.title}</h3>
    <div className="flex mt-1">
      {[...Array(5)].map((_, i) => (
        <Star key={i} size={16} className={i < video.rating ? 'text-yellow-400' : 'text-gray-300'} />
      ))}
    </div>
  </div>
);

const VideoPlayer = ({ video, onClose }) => (
  <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
    <div className="bg-white p-4 rounded-lg w-3/4 h-3/4 relative">
      <button onClick={onClose} className="absolute top-2 right-2 text-gray-500 hover:text-gray-700">
        <X size={24} />
      </button>
      <h2 className="text-xl font-bold mb-4">{video.title}</h2>
      <div className="w-full h-5/6">
        <iframe 
          src={video.link} 
          title={video.title} 
          className="w-full h-full"
          allowFullScreen
        ></iframe>
      </div>
    </div>
  </div>
);

const VideoLibrary = () => {
  const [playingVideo, setPlayingVideo] = useState(null);

  return (
    <div className="p-6">
      {Object.entries(videosByCategory).map(([category, videos]) => (
        <div key={category} className="mb-8">
          <h2 className="text-2xl font-bold mb-4">{category}</h2>
          <div className="flex overflow-x-auto pb-4">
            {videos.map(video => (
              <VideoCard key={video.id} video={video} onPlay={setPlayingVideo} />
            ))}
          </div>
        </div>
      ))}
      {playingVideo && <VideoPlayer video={playingVideo} onClose={() => setPlayingVideo(null)} />}
    </div>
  );
};

export default VideoLibrary;
