import { Star } from 'lucide-react';

const VideoCard = ({ video, onPlay }) => (
    <div className="flex flex-col w-48 m-2 cursor-pointer border">
      <div className="relative w-full h-28 bg-gray-200 rounded-md overflow-hidden flex items-center justify-center" onClick={() => onPlay(video)}>
        {React.isValidElement(video.thumbnail) ? (
          video.thumbnail
        ) : (
          <img src={video.thumbnail} alt={video.title} className="w-full h-full object-cover" />
        )}
      </div>
      <h3 className="mt-2 text-sm font-semibold truncate">{video.title}</h3>
      <div className="flex mt-1">
        {[...Array(5)].map((_, i) => (
          <Star
            key={i}
            size={16}
            fill={i < video.rating ? 'currentColor' : 'none'}
            className={i < video.rating ? 'text-yellow-400' : 'text-gray-300'}
          />
        ))}
      </div>
    </div>
  );

export default VideoLibrary;
