import React from 'react';
import { Star } from 'lucide-react';

const VideoCard = ({ video, onClick }) => {
  const getVideoThumbnail = (url) => {
    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const videoId = url.split('v=')[1] || url.split('/').pop();
      return `https://img.youtube.com/vi/${videoId}/0.jpg`;
    }
    if (url.includes('vimeo.com')) {
      const videoId = url.split('/').pop();
      return `https://vumbnail.com/${videoId}.jpg`;
    }
    if (url.includes('facebook.com')) {
      return `https://www.facebook.com/plugins/video.php?href=${url}&show_text=false&appId`;
    }
    return '/api/placeholder/300/200';
  };

  const thumbnail = getVideoThumbnail(video.link);

  return (
    <div className="video-card" onClick={onClick}>
      <div className="thumbnail-container">
        {thumbnail.includes('facebook.com') ? (
          <iframe
            src={thumbnail}
            className="video-thumbnail"
            style={{ border: 'none', overflow: 'hidden' }}
            width="200"
            height="180"
            allowFullScreen={true}
          />
        ) : (
          <img src={thumbnail} alt={video.title} className="video-thumbnail" width="200" height="180" />
        )}
      </div>
      <div className="video-info">
        <h3 className="video-title">{video.title}</h3>
        <div className="video-rating">
          {[...Array(5)].map((_, i) => (
            <Star
              key={i}
              size={16}
              fill={i < video.rating ? 'currentColor' : 'none'}
              className={i < video.rating ? 'star-filled' : 'star-empty'}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default VideoCard;
