import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';

const AddEditVideo = ({ videos, onSave }) => {
  const { id } = useParams();
  const history = useHistory();
  const [formData, setFormData] = useState({
    title: '',
    category: '',
    link: '',
    thumbnail: null,
    rating: '',
  });

  useEffect(() => {
    if (id && videos) {
      const video = videos.find(v => v.id === id);
      if (video) setFormData(video);
    }
  }, [id, videos]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'file' ? e.target.files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      await fetch('/.netlify/functions/saveVideo', {
        method: 'POST',
        body: formDataToSend,
      });
      onSave();
      history.push('/');
    } catch (error) {
      console.error('Error saving video:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="title" value={formData.title} onChange={handleChange} placeholder="Title" required />
      <input name="category" value={formData.category} onChange={handleChange} placeholder="Category" required />
      <input name="link" value={formData.link} onChange={handleChange} placeholder="Video Link" />
      <input name="thumbnail" type="file" onChange={handleChange} accept="image/*" />
      <input name="rating" type="number" min="0" max="5" value={formData.rating} onChange={handleChange} placeholder="Rating" />
      <button type="submit">Save</button>
    </form>
  );
};

export default AddEditVideo;
