import React, { useState, useEffect } from 'react';
import { Star, X, AlertCircle } from 'lucide-react';

const VideoCard = ({ video, onPlay }) => (
  <div className="flex flex-col w-48 mr-4 cursor-pointer" onClick={() => onPlay(video)}>
    <img src={video.thumbnail} alt={video.title} className="w-full h-32 object-cover rounded-md" />
    <h3 className="mt-2 text-sm font-semibold">{video.title}</h3>
    <div className="flex mt-1">
      {[...Array(5)].map((_, i) => (
        <Star key={i} size={16} className={i < video.rating ? 'text-yellow-400' : 'text-gray-300'} />
      ))}
    </div>
  </div>
);

const VideoPlayer = ({ video, onClose }) => {
  const getEmbedUrl = (url) => {
    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const videoId = url.split('v=')[1] || url.split('/').pop();
      return `https://www.youtube.com/embed/${videoId}`;
    }
    if (url.includes('vimeo.com')) {
      const videoId = url.split('/').pop();
      return `https://player.vimeo.com/video/${videoId}`;
    }
    if (url.includes('facebook.com')) {
      // Facebook videos can't be embedded easily, so we'll return null
      return `https://www.facebook.com/plugins/video.php?href=${url}&width=500&show_text=false&height=889&appId`;
    }
    // Add more video platform handlers here
    return url; // Return original URL if no handler matches
  };

  const embedUrl = getEmbedUrl(video.link);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-4 rounded-lg w-3/4 h-3/4 relative">
        <button onClick={onClose} className="absolute top-2 right-2 text-gray-500 hover:text-gray-700">
          <X size={24} />
        </button>
        <h2 className="text-xl font-bold mb-4">{video.title}</h2>
        <div className="w-full h-5/6">
          {embedUrl ? (
            <iframe 
              src={embedUrl} 
              title={video.title} 
              height="500"
              className="w-full h-full"
              allowFullScreen
            ></iframe>
          ) : (
            <div className="w-full h-full flex items-center justify-center bg-gray-100">
              <div className="text-center">
                <AlertCircle size={48} className="mx-auto mb-4 text-yellow-500" />
                <p>This video cannot be embedded.</p>
                <a href={video.link} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline mt-2 inline-block">
                  Open video in new tab
                </a>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const VideoLibrary = () => {
  const [videosByCategory, setVideosByCategory] = useState({});
  const [playingVideo, setPlayingVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      setLoading(true);
      const response = await fetch('/.netlify/functions/getVideos');
      if (!response.ok) throw new Error('Failed to fetch videos');
      const data = await response.json();

      const groupedVideos = data.reduce((acc, video) => {
        if (!acc[video.category]) {
          acc[video.category] = [];
        }
        acc[video.category].push({
          ...video,
          thumbnail: getVideoThumbnail(video.link)
        });
        return acc;
      }, {});

      setVideosByCategory(groupedVideos);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const getVideoThumbnail = (url) => {
    if (url.includes('youtube.com') || url.includes('youtu.be')) {
      const videoId = url.split('v=')[1] || url.split('/').pop();
      return `https://img.youtube.com/vi/${videoId}/0.jpg`;
    }
    if (url.includes('vimeo.com')) {
      const videoId = url.split('/').pop();
      return `https://vumbnail.com/${videoId}.jpg`;
    }
    if (url.includes('facebook.com')) {
      // Facebook doesn't provide an easy way to get thumbnails
      // You might want to use a default Facebook video thumbnail here
      return '/facebook-video-thumbnail.jpg';
    }
    // Add more video platform handlers here
    return '/api/placeholder/300/200'; // Default placeholder
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="p-6">
      {Object.entries(videosByCategory).map(([category, videos]) => (
        <div key={category} className="mb-8">
          <h2 className="text-2xl font-bold mb-4">{category}</h2>
          <div className="flex overflow-x-auto pb-4">
            {videos.map(video => (
              <VideoCard key={video.id} video={video} onPlay={setPlayingVideo} />
            ))}
          </div>
        </div>
      ))}
      {playingVideo && <VideoPlayer video={playingVideo} onClose={() => setPlayingVideo(null)} />}
    </div>
  );
};

export default VideoLibrary;
