import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const VideoLibrary = ({ onUpdate }) => {
  const [videos, setVideos] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('/.netlify/functions/getVideos');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (Array.isArray(data)) {
        setVideos(data);
      } else {
        console.error('Received non-array data:', data);
        setVideos([]);
      }
    } catch (error) {
      console.error('Error fetching videos:', error);
      setError(error.message);
      setVideos([]);
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch('/.netlify/functions/deleteVideo', {
        method: 'POST',
        body: JSON.stringify({ id }),
      });
      fetchVideos();
      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error deleting video:', error);
    }
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  const categories = [...new Set(videos.map(video => video.category))];

  return (
    <div className="video-library">
      <h1>DIY Video Library</h1>
      {categories.map(category => (
        <div key={category} className="category">
          <h2>{category}</h2>
          <div className="video-scroll">
            {videos
              .filter(video => video.category === category)
              .map(video => (
                <div key={video.id} className="video-card">
                  {video.thumbnail && <img src={video.thumbnail} alt={video.title} />}
                  <h3>{video.title}</h3>
                  <p>Rating: {video.rating}/5</p>
                  <Link to={`/edit/${video.id}`}>Edit</Link>
                  <button onClick={() => handleDelete(video.id)}>Delete</button>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default VideoLibrary;
