import React, { useState, useEffect } from 'react';
import './App.css';

const VideoCard = ({ title, thumbnail, category, rating }) => (
  <div className="video-card">
    <img src={thumbnail} alt={title} />
    <h3>{title}</h3>
    <p>Category: {category}</p>
    <p>Rating: {rating}/5</p>
  </div>
);

const App = () => {
  const [videos, setVideos] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch video data from JSON file or API
    // This is a placeholder for demonstration
    setVideos([
      { id: 1, title: 'Avocado Seed', category: 'Plants', rating: 4, thumbnail: 'placeholder.jpg' },
      { id: 2, title: 'Grape Toner', category: 'Skincare', rating: 5, thumbnail: 'placeholder.jpg' },
      // Add more video objects here
    ]);

    setCategories(['Plants', 'Skincare', 'Health', 'DIY organisers', 'Kitchen']);
  }, []);

  return (
    <div className="app">
      <h1>DIY Video Library</h1>
      {categories.map(category => (
        <div key={category} className="category">
          <h2>{category}</h2>
          <div className="video-grid">
            {videos
              .filter(video => video.category === category)
              .map(video => (
                <VideoCard key={video.id} {...video} />
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
