import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import { Menu } from 'lucide-react';
import VideoLibrary from './components/VideoLibrary';
import AddEditVideo from './components/AddEditVideo';
import './App.css';

const App = () => {
  const [videos, setVideos] = useState([]);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('/.netlify/functions/getVideos');
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  return (
    <Router>
      <div className="app">
        <nav className="navbar">
          <Link to="/" className="logo">DIY Videos</Link>
          <button className="menu-button" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <Menu size={24} />
          </button>
          {isMenuOpen && (
            <div className="menu">
              <Link to="/" onClick={() => setIsMenuOpen(false)}>Video Library</Link>
              <Link to="/add" onClick={() => setIsMenuOpen(false)}>Add New Video</Link>
            </div>
          )}
        </nav>

        <Routes>
          <Route path="/" element={<Sample videos={videos} onUpdate={fetchVideos} />} />
          <Route path="/add" element={<AddEditVideo onSave={fetchVideos} />} />
          <Route path="/edit/:id" element={<AddEditVideo videos={videos} onSave={fetchVideos} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
