import React, { useState, useEffect } from 'react';
import VideoLibrary from './VideoLibrary_v2';
import VideoPlayer from './VideoPlayer_v2';

const App = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [categories, setCategories] = useState({});

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('/.netlify/functions/getVideo');
      const data = await response.json();

      setVideos(data);

      // Categorize videos
      const categorizedVideos = data.reduce((acc, video) => {
        const category = video.category || 'Uncategorized';
        if (!acc[category]) acc[category] = [];
        acc[category].push(video);
        return acc;
      }, {});
      setCategories(categorizedVideos);

    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  const handleVideoClick = (video) => {
    setSelectedVideo(video);
  };

  const handleVideoClose = () => {
    setSelectedVideo(null);
  };

  return (
    <div className="app">
      {selectedVideo ? (
        <VideoPlayer video={selectedVideo} onClose={handleVideoClose} />
      ) : (
        <VideoLibrary categories={categories} onVideoClick={handleVideoClick} />
      )}
    </div>
  );
};

export default App;



